import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import BecomeInstructor from './BecomeInstructor';
import Goals from './Goals';
import Interface from './Interface';
import Navbar from './Navbar';
import Team from './Team';
import TopCategories from './TopCategories';
import Course from './Course';
import CategoryCourses from './CategoryCourses';
import AllCourses from './AllCourses';

const Home = () => {
	const [categoryName, setCategoryName] = useState([]);

	return (
		<Router>
			<Route path='/' render={(props) => (<Navbar {...props} />)} />
			<Switch>
				
			</Switch>
			<Route path='/courses/:id' render={(props) => <Course {...props} />} />
			<Route exact path='/categories' render={(props) => (<TopCategories {...props} />)} />
			<Route path='/categories/:id' render={(props) => (<CategoryCourses {...props} categoryName={categoryName} />)} />
			<Route exact path='/courses' render={(props) => (<AllCourses {...props} />)} />
			<Interface />
			<Goals />
			<BecomeInstructor />
			<Team />
		</Router>
	);
};

export default Home;



