import React ,{useContext } from 'react';
import './App.css';
import { BrowserRouter as Router ,Route} from 'react-router-dom';
import Home from './components/Home';
import LogIn from './components/LogIn';
import Course from './components/Course ';

const App = () => {
	const [categories, setCategories] = useState([]);
	const [category, setCategory] = useState([]);
	const [id, setId] = useState();
  
	useEffect(() => { getTopCategory() }, []);
	useEffect(() => { coursesCategory() }, [id]);
  
	const categoriesName = categories.map((e) => e.name);
	const categoriesId = categories.map((e) => e.id);
  
	const getTopCategory = () => {
	  axios.get('http://localhost:5000/students/categories')
		.then((res) => {
		  console.log('res.data', res.data)
		  setCategories(res.data);
		})
		.catch((err) => {
		  console.log('ERR: ', err);
		})
	};
  
	const coursesCategory = () => {
	  axios.get(`http://localhost:5000/students/category_courses/${id}`)
		.then((res) => {
		  console.log('res.data', res.data)
		  setCategory(res.data);
		})
		.catch((err) => {
		  console.log('ERR: ', err);
		})
	};
	return (
		<Router>
			<Home />
			<LogIn />
 			<Route exact path='/courses/:id' render={(props) => <Course  {...props} />} />
		</Router>
	);
};

export default App;
