import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
const token = localStorage.getItem('token');
const decoded = jwt_decode(token);
let stuId = decoded.id;

const Course = ({ match: { params: { id }, }, }) => {
	const [course, setCourse] = useState([]);
	const [enrollmentCourses, setEnrollmentCourses] = useState([]);

	useEffect(() => {
		axios
			.get(`http://localhost:5000/students/details/${id}`, {
				headers: { authorization: token },
			})
			.then((res) => {
				setCourse(...res.data);
			})
			.catch((err) => {
				console.log('ERR: ', err);
			});
	}, [id]);

	useEffect(() => {
			axios.get(`http://localhost:5000/students/history/${stuId}`)
				.then((response) => {
					setEnrollmentCourses(response.data);
				})
				.catch((err) => { console.log('err :', err) });
	}, [stuId]);

	console.log('course :', course)
	console.log('enrollmentCourses :', enrollmentCourses)
	// console.log(enrollmentCourses.indexOf(course)); //true
const arr = enrollmentCourses.filter((e)=>{ return e === course})
console.log('arr :', arr)
	return (
		<div className='course'>
			<div>
				<img src={`${course.img_url}`} alt={`${course.name}`} />
			</div>
			<div className='course-details'>
				<h2> {' '} <span>Course Name : </span> {course.name}{' '} </h2>
				<h3> {' '} <span>Course Description : </span> {course.description} </h3>
				<h3> {' '} <span>Price: </span> {course.price + ' $'}{' '} </h3>
			</div>
		</div>
	);
};

export default Course;
