import React, { useState, useEffect } from 'react';
import axios from "axios";
import android from './pics/android.jpg';
import networks from './pics/networks.png';
import webdesign from './pics/webdesign.jpg';
import support from './pics/support.jpg';
import ios from './pics/ios.png';
import webdev from './pics/webdev.jpg';

const TopCategories = () => {
  const [category, setCategory] = useState([]);
  useEffect(() => { 
    getTopCategory();
   }, []);

  const getTopCategory = () => {
    axios.get('http://localhost:5000/students/categories')
      .then((res) => {
        setCategory(res.data);
      })
      .catch((err) => {
        console.log('ERR: ', err);
      })
  };


  const getData = useCallback ( () => { 
    setTimeout(() => {
      console.log("Inside setTimeOut");
      setCategory("HEllo");
    }, 2000);

  }, [] )

  useEffect(() => {
    getData()
   },[getData]);

  console.log('category', category[0])

  return (
    <div>
      <h1 className='tt'>Top categories</h1>
      <div className='card'>
        <img src={webdesign}></img>
        <h2>{category[0]}</h2>
      </div>
      <div className='card'>
        <img src={webdev}></img>
        <h2>{category[1]}</h2>
      </div>
      <div className='card'>
        <img src={networks}></img>
        <h2>{category[2]}</h2>
      </div>
      <div className='card'>
        <img src={support}></img>
        <h2>{category[3]}</h2>
      </div>
      <div className='cards'>
        <div className='card'>
          <img src={android}></img>
          <h2>{category[4]}</h2>
        </div>
        <div className='card'>
          <img src={ios}></img>
          <h2>{category[5]}</h2>
        </div>

      </div>
    </div>
  )
}

export default TopCategories
