import React from 'react';
import android from './pics/android.jpg';

const Course = ({ match: { params: { id } } }) => {
	const [course, setCourse] = useState([]);
	useEffect(() => {
		axios.get(`http://localhost:5000/students/details/${match.params.id}`)
			.then((res) => {
				setCourse(...res.data);
			})
			.catch((err) => {
				console.log('ERR: ', err);
			})
	}, [match.params.id]);
	return (
		<div className='course'>
			<div>
				<img src={android}></img>
			</div>
			<div className='course-details'>
				<h2>{course.name}</h2>
				<h3>{course.description}</h3>
				<h3>{course.instructor_id}</h3>
				<h3>{course.price + ' $'}</h3>
			</div>
			{/* {
				props.course.map((e, i) => (
					<div className='course-details'>
						<h2> <span>Course Name : </span> {e.name} </h2>
						<h3> <span>Course Description : </span> {e.description}</h3>
						<h3> <span>Price: </span> {e.price + ' $'} </h3>
					</div>
				))
			} */}
		</div>
	);
};

export default Course;
