import React, { useState, useEffect } from 'react';
import './App.css';
import axios from "axios";

import { BrowserRouter as Router, Route } from 'react-router-dom';
import Home from './components/Home';
import LogIn from './components/LogIn';
import Course from './components/Course ';
import CategoryCourses from './components/Category_courses';

const App = () => {
	const [category, setCategory] = useState([]);
	const [courses, setCourses] = useState([]);
	const [course, setCourse] = useState([]);

	useEffect(() => { coursesCategory() }, []);
	useEffect(() => { getAllCourses() }, []);
	useEffect(() => { oneCourse() }, []);

	const getAllCourses = () => {
		axios.get('http://localhost:5000/students/courses')
			.then((res) => {
				setCourses(res.data);
			})
			.catch((err) => {
				console.log('ERR: ', err);
			})
	};
	const coursesCategory = (id) => {
		axios.get(`http://localhost:5000/students/category_courses/${id}`)
			.then((res) => {
				setCategory(res.data);
			})
			.catch((err) => {
				console.log('ERR: ', err);
			})
	};
	
	const oneCourse = () => {
		axios.get(`http://localhost:5000/students/details/${id}`)
			.then((res) => {
				console.log('object', res.data)
				setCourse(res.data);
			})
			.catch((err) => {
				console.log('ERR: ', err);
			})
	};

	return (
		<Router>
			<Home id={coursesCategory} />
			<LogIn />
			<Route exact path='/courses/:id' render={(props) => <CategoryCourses  {...props} category={category} id={coursesCategory} />} />
			<Route exact path='/course/:id' render={(props) => <Course  {...props} course={course}/>} />
		</Router>
	);
};

export default App;
