import React, { useState, useEffect } from 'react';
import axios from "axios";
import { Link, Router } from 'react-router-dom';
import logo from './pics/logo.png';
import search from './pics/search.png';

const Navbar = () => {
	const [categories, setCategories] = useState([]);

	const getAllCategories = () => {
		axios
			.get(`http://localhost:5000/students/categories`)
			.then((response) => {
				setCategories(response.data);
			})
			.catch((err) => { console.log('err :', err) });
	};

	useEffect(() => {
		getAllCategories();
	}, []);

	return (
		<Router>
			<nav className='navbar'>
				<img src={logo} className='logo'></img>
				<div className='dropdown'>
					<div className='drop-button'>Categories</div>
					<div className='dropdown-content'>
						{<Link to={`/courses/`} > All Courses </Link>}
						{<Link to={`/categories/`} > All Categories </Link>}
						{
							categories.map((e, i) => {
								return (
									<Link to={`/categories/${e.id}`} key={i}>
										{e.name}
									</Link>
								);
							})
						}
					</div>
				</div>
				<div className='search'>
					<img src={search}></img>
					<input placeholder='Search for anything'></input>
				</div>
				<h4>Teach On Grade-A</h4>
				<button className='log'>Log in</button>
				<button className='sign'>Sign up</button>
			</nav>
			<Route exact path='/result' render={(props) => <Result {...props} result={result} input={key} />} />
		</Router>
	);
};

export default Navbar;
