import React, { useState, useEffect } from 'react';
import { useHistory, } from "react-router-dom";
import axios from "axios";
import { Link } from 'react-router-dom';
import { RiFilter3Line } from "react-icons/ri";
const token = localStorage.getItem('token');
 
export default function Courses() {
	const [courses, setCourses] = useState();
	useEffect(() => {
		axios
			.get('http://localhost:5000/instructors/courses', {
				headers: { authorization: token },
			})
			.then((res) => {
				setCourses(res.data);
			})
			.catch((err) => {});
	}, []);
	return (
		<>
			<div>
				<div>
					<ul className='unOrderListStyle'>
						<li className='listItemStyle'>
							<k className='linkStyle' to='/courses' href='#about'>
								Get All courses
							</k>
						</li>
						<li className='listItemStyle'>
							<Link className='linkStyle' to='/newcourse' href='#about'>
								Add Course
							</Link>
						</li>
					</ul>
				</div>
			</div>

			<div>
				<Route path='/courses'>
					<div>
						<GitAllCourse courses={courses}></GitAllCourse>
					</div>
				</Route>
				<Route path='/newcourse'>
					<AddNewCourse></AddNewCourse>
				</Route>
			</div>
		</>
	);
}

