import React from 'react';
import { BrowserRouter as Router, Link } from 'react-router-dom';
import Course from './Course '
const CategoryCourses = (props) => {
  return (
    <div>
      {
        props.category.map((e, i) => {
          return <Router key={i}>
             <Link to={`/course/${e.id}`}>
              <div className='course' onClick={() => { props.i(e.id) }, <Course />}>{e.name}</div>
              <div className="infoPhoto"></div>
            </Link>
          </Router>
     
        })
      }
    </div>
  );
};
export default CategoryCourses;
