import React, { useState, useEffect, useCallback } from 'react';
import axios from "axios";
import android from './pics/android.jpg';
import networks from './pics/networks.png';
import webdesign from './pics/webdesign.jpg';
import support from './pics/support.jpg';
import ios from './pics/ios.png';
import webdev from './pics/webdev.jpg';

const TopCategories = () => {
  const [category, setCategory] = useState([]);


  useEffect(() => { 
     getTopCategory()
  }, []); 

  const categoryName = category.map((e) => e.name)
  console.log('category', category)
  return (
    <div>
      <h1 className='tt'>Top categories</h1>
      <div className='card'   >
        <img src={webdesign}></img>
        <h2>{categoryName[0]}</h2>
      </div>
      <div className='card'>
        <img src={webdev}></img>
        <h2>{categoryName[1]}</h2>
      </div>
      <div className='card'>
        <img src={networks}></img>
        <h2>{categoryName[2]}</h2>
      </div>
      <div className='card'>
        <img src={support}></img>
        <h2>{categoryName[3]}</h2>
      </div>
      <div className='card'>
        <img src={android}></img>
        <h2>{categoryName[4]}</h2>
      </div>
      <div className='card'>
        <img src={ios}></img>
        <h2>{categoryName[5]}</h2>
      </div >
    </div>
  )
}

export default TopCategories
