import React, { Component, useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, NavLink } from 'react-router-dom';
import axios from 'axios';
import logo from './pics/logo.png';
import search from './pics/search.png';
import { Result } from './Result';

const Navbar = () => {
  const [result, setResult] = useState([]);
  const [textInput, setTextInput] = useState('');
  const [courses, setCourses] = useState([]);
  useEffect(() => { getAllCourses() }, []);

  const getAllCourses = () => {
    axios.get('http://localhost:5000/students/courses')
      .then((res) => {
        console.log('res.data', res.data)
        setCourses(res.data);
      })
      .catch((err) => {
        console.log('ERR: ', err);
      })
  };

  const searchCourses = (i) => {
    axios.get(`http://localhost:5000/students/search/${i}`)
      .then((res) => {
        console.log('res.data', res.data)
          setResult(res.data);
      })
      .catch((err) => {
        console.log('ERR: ', err);
      })
  };

  const handleInputChange = (e) => {    
      if (e.key === 'Enter') {setTextInput(e.target.value) ;
      console.log('textInput:', e.target.value)
      searchCourses(textInput) }
  };

  return (
    <Router> 
      <div className='search'>
      <img src={search}></img>
      <Link to='/result'>
        <input to='/result' placeholder='Search for anything' onKeyPress={handleInputChange}></input>
     </Link>
      </div>
      <Route path='/result'><Result result={result}/></Route>   
    </Router>
  )
}

export default Navbar
