import React, { Component ,useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import axios from 'axios';
import logo from './pics/logo.png';
import search from './pics/search.png';

const Navbar = () => {
  const [searchP, setSearchP] = useState([]);
	const [courses, setCourses] = useState([]);
	useEffect(() => {getAllCourses()}, []);

	const getAllCourses =  () => {
    axios.get('http://localhost:5000/students/courses')
    .then((res)=>{
		console.log('res.data', res.data)
     setCourses(res.data);
    }) 
	  .catch ((err) =>{
		console.log('ERR: ', err);
	  })
  }; 
	const searchProducts = async (i) => {
		try {
			const res = await axios.get(`http://localhost:5000/searchProducts/${i}`)
			if (res.data.length) {
				await setSearchP(res.data);
			} else {
				return "Not found";
			}
		}
		catch (err) {
			console.log('ERR: ', err);
		};
  };
  
  return (
	<Router>
    	<div className='search'>
						<img src={search}></img>
						<input placeholder='Search for anything'onKeyPress={(e)=> {if (e.key === 'Enter')console.log(5)}}></input>
					</div>
	  <Route />
	</Router>
  )
}

export default Navbar
