import React, { useState, useEffect } from 'react';
import { Route, Link } from 'react-router-dom';
import axios from "axios";
import android from './pics/android.jpg';
import networks from './pics/networks.png';
import webdesign from './pics';
import support from './pics/support.jpg';
import ios from './pics/ios.png';
import webdev from './pics/webdev.jpg';

const TopCategories = () => {
  const [categories, setCategories] = useState([]);
  const [category, setCategory] = useState([]);
  const [id, setId] = useState();

  useEffect(() => { getTopCategory() }, []);
  useEffect(() => { coursesCategory() }, [id]);

  const categoriesName = categories.map((e) => e.name);
  const categoriesId = categories.map((e) => e.id);

  const getTopCategory = () => {
    axios.get('http://localhost:5000/students/categories')
      .then((res) => {
        console.log('res.data', res.data)
        setCategories(res.data);
      })
      .catch((err) => {
        console.log('ERR: ', err);
      })
  };

  const coursesCategory = () => {
    axios.get(`http://localhost:5000/students/category_courses/${id}`)
      .then((res) => {
        console.log('res.data', res.data)
        setCategory(res.data);
      })
      .catch((err) => {
        console.log('ERR: ', err);
      })
  };

  return (
    <div>
      <h1 className='tt'>Top categories</h1>
      <div className='cards'>
      < Link to={`/courses/${categoriesId[4]}`} >
        <div className='card' onClick={() => setId(categoriesId[4])}>
          <img src={android} alt=''></img>
          <h2>{categoriesName[4]}</h2>
        </div>
                </Link >
      < Link to={`/courses/${categoriesId[2]}`} >
        <div className='card' onClick={() => setId(categoriesId[2])} >
          <img src={networks} alt=''></img>
          <h2>{categoriesName[2]}</h2>
        </div>
      </Link >
      < Link to={`/courses/${categoriesId[0]}`} >
        <div className='card' onClick={() => setId(categoriesId[0])}>
          <img src={webdesign} alt=''></img>
          <h2>{categoriesName[0]}</h2>
        </div>
      </Link >
      < Link to={`/courses/${categoriesId[3]}`} >
        <div className='card' onClick={() => setId(categoriesId[3])}>
          <img src={support} alt=''></img>
          <h2>{categoriesName[3]}</h2>
        </div>
      </Link >
      < Link to={`/courses/${categoriesId[1]}`} >
        <div className='card' onClick={() => setId(categoriesId[5])}>
          <img src={ios} alt=''></img>
          <h2>{categoriesName[5]}</h2>
        </div>
      </Link >
      < Link to={`/courses/${categoriesId[1]}`} >
        <div className='card' onClick={() => setId(categoriesId[1])}>
          <img src={webdev} alt=''></img>
          <h2>{categoriesName[1]}</h2>
        </div>
      </Link >
    </div>
      </div >
  )
}

export default TopCategories
