import React, { Component, useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import axios from 'axios';
import logo from './pics/logo.png';
import search from './pics/search.png';
import { Result } from './Result';

const Navbar = () => {
  const [result, setResult] = useState([]);
  const [courses, setCourses] = useState([]);
  useEffect(() => { getAllCourses() }, []);

  const getAllCourses = () => {
    axios.get('http://localhost:5000/students/courses')
      .then((res) => {
        console.log('res.data', res.data)
        setCourses(res.data);
      })
      .catch((err) => {
        console.log('ERR: ', err);
      })
  };

  const searchProducts = (i) => {
    axios.get(`http://localhost:5000/students/search/${i}`)
      .then((res) => {
        console.log('res.data', res.data)
        if (res.data.length) {
          setResult(res.data);
        } else {
          setResult('Not found');
        }
      })
      .catch((err) => {
        console.log('ERR: ', err);
      })
  };

  return (
    <Router>
      <div className='search'>
        <img src={search}></img>
        <input placeholder='Search for anything' onKeyPress={(e) => { 
          if (e.key === 'Enter') searchProducts(e.target.value) ;e.target.value=''}}></input>
      </div>result
      {/* <Route path="/result"><Result result={result}/></Route>    */}
      <Route path="/searchProducts" render={(props) => <Result {...props} {...result} search={searchProducts} product={oneProduct} />} />
      <Route />
    </Router>
  )
}

export default Navbar
