import React from 'react';
import {Link} from 'react-router-dom';
// import Course from './Course '
const CategoryCourses = (props) => {
  const [category, setCategory] = useState([]);
  useEffect(() => { coursesCategory() }, []);

  const coursesCategory = () => {
      axios.get(`http://localhost:5000/students/category_courses/${props.match.params.id}`)
          .then((res) => {
              console.log('oneCategory:', res.data)
              setCategory(...res.data);
          })
          .catch((err) => {
              console.log('ERR: ', err);
          })
  };

  return (
    <div className="product-list">
      {
        props.category.map((e, i) => {
          return <div key={i}>
            <Link to={`/course/${e.id}`}>
              <div className="course" >
                <div className="infoPhoto">{e.name}</div>
              </div>
            </Link>
          </div>
        })
      }
    </div>
  );
};
export default CategoryCourses;
